import React, { useEffect, useState } from 'react';
import { motion } from 'framer-motion';
import PageTransition from '../components/PageTransition';
import Navigation from '../components/Navigation';
import { Pie } from 'react-chartjs-2';

function Slide4_Genres() {
  const [stats, setStats] = useState(null);
  const [quip, setQuip] = useState('');
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchGenresAndQuip = async () => {
      try {
        const statsRes = await fetch('/api/stats/me');
        const userStats = await statsRes.json();
        const stats = Array.isArray(userStats) ? userStats[0] : userStats;
        setStats(stats);

        const quipRes = await fetch('/api/generate-quip', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify({
            statDescription: `Your top genres are ${stats.topGenres.join(', ')}`,
          }),
        });
        const quipData = await quipRes.json();
        setQuip(quipData.quip);
      } catch (err) {
        setError('Failed to load top genres or generate quip.');
      }
    };
    fetchGenresAndQuip();
  }, []);

  if (error) {
    return (
      <PageTransition title="Error">
        <p className="text-red-400">{error}</p>
      </PageTransition>
    );
  }

  if (!stats) {
    return (
      <PageTransition title="Loading">
        <p>Loading genres...</p>
      </PageTransition>
    );
  }

  const genreCount = stats.topGenres.length;
  const chartData = {
    labels: stats.topGenres,
    datasets: [
      {
        data: stats.topGenres.map(() => Math.floor(Math.random() * 100)),
        backgroundColor: Array(genreCount).fill('#1DB954'),
      },
    ],
  };

  return (
    <PageTransition title="Your Top Genres">
      <div className="flex flex-col items-center justify-center min-h-screen px-4">
        <div className="w-full max-w-[90%] md:max-w-[70%] lg:max-w-[40%] mb-6">
          <Pie data={chartData} />
        </div>
        <p className="text-spotify-green italic text-center mb-4">
          {quip}
        </p>
      </div>
      <Navigation currentSlide={4} />
    </PageTransition>
  );
}

export default Slide4_Genres;
