// src/pages/Slide5_Clock.jsx
import React, { useEffect, useState } from 'react';
import { Box, Text, Button, VStack } from '@chakra-ui/react';
import axios from 'axios';
import PageTransition from '../components/PageTransition';

/*
  Example: stats.listeningClock = {
    morning: [ 'SongA', 'SongB' ],
    afternoon: [ 'SongC' ],
    evening: [ 'SongD' ],
    night: [ 'SongE', 'SongF' ]
  }
*/

function Slide5_Clock() {
  const [stats, setStats] = useState(null);
  const [quip, setQuip] = useState('');
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchClockAndQuip = async () => {
      try {
        const statsRes = await axios.get('/api/stats/me');
        const userStats = Array.isArray(statsRes.data)
          ? statsRes.data[0]
          : statsRes.data;
        setStats(userStats);

        const quipRes = await axios.post('/api/generate-quip', {
          statDescription: 'Your Listening Clock distribution of songs by daypart.',
        });
        setQuip(quipRes.data.quip);
      } catch (err) {
        setError('Failed to load listening clock or quip.');
      }
    };
    fetchClockAndQuip();
  }, []);

  const nextSlide = () => (window.location.href = '/slide6');

  if (error) {
    return (
      <PageTransition>
        <Text color="red.300">{error}</Text>
      </PageTransition>
    );
  }

  if (!stats) {
    return (
      <PageTransition>
        <Text>Loading Clock Data...</Text>
      </PageTransition>
    );
  }

  const { morning = [], afternoon = [], evening = [], night = [] } = stats.listeningClock || {};

  return (
    <PageTransition>
      <Box
        minH="100vh"
        display="flex"
        flexDirection="column"
        alignItems="center"
        justifyContent="center"
        px={6}
      >
        <Text fontSize="3xl" fontWeight="bold" mb={6}>
          Listening Clock
        </Text>
        <VStack spacing={4}>
          <Text>Morning: {morning.join(', ')}</Text>
          <Text>Afternoon: {afternoon.join(', ')}</Text>
          <Text>Evening: {evening.join(', ')}</Text>
          <Text>Night: {night.join(', ')}</Text>
        </VStack>
        <Text color="spotify.green" fontStyle="italic" textAlign="center" mt={4}>
          {quip}
        </Text>
        <Button
          bg="spotify.green"
          color="spotify.black"
          mt={6}
          _hover={{ bg: 'green.400', transform: 'scale(1.05)' }}
          onClick={nextSlide}
        >
          Next
        </Button>
      </Box>
    </PageTransition>
  );
}

export default Slide5_Clock;
