import { createContext, useContext, useState, useEffect } from 'react';
import { fetchSpotifyDataWithQuip } from '../utils/api';

const SpotifyDataContext = createContext(null);

export const SpotifyDataProvider = ({ children }) => {
  const [spotifyData, setSpotifyData] = useState(null);
  const [quip, setQuip] = useState('');
  const [error, setError] = useState(null);
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const data = await fetchSpotifyDataWithQuip();
        setSpotifyData(data.stats);
        setQuip(data.quip);
        setIsLoading(false);
      } catch (err) {
        setError(err);
        setIsLoading(false);
      }
    };

    fetchData();
  }, []);

  const updateQuip = async (statDescription) => {
    try {
      const data = await fetchSpotifyDataWithQuip(statDescription);
      setQuip(data.quip);
    } catch (err) {
      setError(err);
    }
  };

  return (
    <SpotifyDataContext.Provider value={{ 
      spotifyData, 
      quip, 
      error, 
      isLoading,
      updateQuip 
    }}>
      {children}
    </SpotifyDataContext.Provider>
  );
};

export const useSpotifyData = () => {
  const context = useContext(SpotifyDataContext);
  if (!context) {
    throw new Error('useSpotifyData must be used within a SpotifyDataProvider');
  }
  return context;
};
