import React from 'react';
import { motion } from 'framer-motion';
import PageTransition from '../components/PageTransition';
import Navigation from '../components/Navigation';
import { useSpotifyData } from '../context/SpotifyDataContext';

function Slide3_Albums() {
  const { spotifyData, quip, error, isLoading } = useSpotifyData();

  const albums = spotifyData?.topAlbums || [];

  return (
    <PageTransition title="Your Top Albums">
      <div className="flex flex-col items-center justify-center min-h-screen px-4">
        {error ? (
          <p className="text-red-400">{error}</p>
        ) : isLoading ? (
          <p>Loading albums...</p>
        ) : albums.length > 0 ? (
          <motion.div 
            initial={{ opacity: 0 }} 
            animate={{ opacity: 1 }}
            className="w-full max-w-4xl"
          >
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4 mb-6">
              {albums.map((album, index) => (
                <motion.div
                  key={`album-${index}`}
                  initial={{ opacity: 0, y: 20 }}
                  animate={{ opacity: 1, y: 0 }}
                  transition={{ delay: index * 0.1 }}
                  className="bg-spotify-gray/20 p-4 rounded-lg text-center"
                >
                  <span className="text-spotify-white text-lg">
                    {album}
                  </span>
                </motion.div>
              ))}
            </div>
            {quip && (
              <p className="text-spotify-green italic text-center text-lg mt-6">
                {quip}
              </p>
            )}
          </motion.div>
        ) : (
          <p className="text-spotify-white">No albums found</p>
        )}
        <Navigation currentSlide={3} />
      </div>
    </PageTransition>
  );
}

export default Slide3_Albums;
