// src/pages/Slide10_Final.jsx
import React, { useEffect, useState } from 'react';
import {
  Box,
  Text,
  Button,
  VStack
} from '@chakra-ui/react';
import axios from 'axios';
import PageTransition from '../components/PageTransition';
import {
  FacebookShareButton,
  TwitterShareButton,
  WhatsappShareButton,
  FacebookIcon,
  TwitterIcon,
  WhatsappIcon,
} from 'react-share';

function Slide10_Final() {
  const [quip, setQuip] = useState('');
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchQuip = async () => {
      try {
        const quipRes = await axios.post('/api/generate-quip', {
          statDescription: 'Final summary of your Spotify Wrapped!',
        });
        setQuip(quipRes.data.quip);
      } catch (err) {
        setError('Failed to generate final quip.');
      }
    };
    fetchQuip();
  }, []);

  const shareUrl = 'https://my-better-spotify-wrapped.com'; // or your deployed domain

  if (error) {
    return (
      <PageTransition>
        <Text color="red.400">{error}</Text>
      </PageTransition>
    );
  }

  return (
    <PageTransition>
      <Box
        minH="100vh"
        display="flex"
        flexDirection="column"
        alignItems="center"
        justifyContent="center"
        px={4}
        textAlign="center"
      >
        <VStack spacing={6}>
          <Text fontSize="3xl" fontWeight="bold">
            Thank You for Exploring Your Better Spotify Wrapped!
          </Text>
          <Text color="spotify.green" fontStyle="italic">
            {quip}
          </Text>
          <Text>Share your stats with friends:</Text>
          <Box display="flex" gap={4} justifyContent="center">
            <FacebookShareButton url={shareUrl} quote="Check out my Spotify Wrapped!">
              <FacebookIcon size={40} round />
            </FacebookShareButton>
            <TwitterShareButton url={shareUrl} title="Check out my Spotify Wrapped!">
              <TwitterIcon size={40} round />
            </TwitterShareButton>
            <WhatsappShareButton url={shareUrl} title="Check out my Spotify Wrapped!">
              <WhatsappIcon size={40} round />
            </WhatsappShareButton>
          </Box>
          <Button
            bg="spotify.green"
            color="spotify.black"
            _hover={{ bg: 'green.400', transform: 'scale(1.05)' }}
            onClick={() => (window.location.href = '/')}
          >
            Return to Home
          </Button>
        </VStack>
      </Box>
    </PageTransition>
  );
}

export default Slide10_Final;
