import { createContext, useContext, useState, useEffect } from 'react';
import { fetchSpotifyDataForYear } from '../utils/api';

const SpotifyDataContext = createContext(null);

export const SpotifyDataProvider = ({ children }) => {
  const [selectedYear, setSelectedYear] = useState(null);
  const [data, setData] = useState({
    stats: null,
    quip: ''
  });
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    if (selectedYear) {
      const loadData = async () => {
        try {
          setIsLoading(true);
          const response = await fetchSpotifyDataForYear(selectedYear);
          setData(response);
        } catch (err) {
          setError('Failed to load Spotify data');
        } finally {
          setIsLoading(false);
        }
      };
      loadData();
    }
  }, [selectedYear]);

  return (
    <SpotifyDataContext.Provider value={{
      spotifyData: data.stats,
      quip: data.quip,
      isLoading,
      error,
      selectedYear,
      setSelectedYear
    }}>
      {children}
    </SpotifyDataContext.Provider>
  );
};

export const useSpotifyData = () => {
  const context = useContext(SpotifyDataContext);
  if (!context) {
    throw new Error('useSpotifyData must be used within SpotifyDataProvider');
  }
  return context;
};
