import axios from 'axios';

const api = axios.create({
  baseURL: 'http://localhost:3001/api',
  withCredentials: true,
});

export const fetchSpotifyData = async () => {
  const response = await api.get('/fetch-spotify-data');
  return response.data;
};

export const fetchUserProfile = async () => {
  const response = await api.get('/user-profile');
  return response.data;
};

export const fetchTopArtists = async () => {
  const response = await api.get('/stats/me/artists');
  return response.data;
};

export const fetchTopTracks = async () => {
  const response = await api.get('/stats/me/tracks');
  return response.data;
};

export const generateQuip = async (statDescription) => {
  const response = await api.post('/generate-quip', { statDescription });
  return response.data;
};

export default api;
