// src/pages/Slide4_Genres.jsx
import React, { useEffect, useState } from 'react';
import { Box, Text, Button } from '@chakra-ui/react';
import axios from 'axios';
import { Pie } from 'react-chartjs-2';
import PageTransition from '../components/PageTransition';

function Slide4_Genres() {
  const [stats, setStats] = useState(null);
  const [quip, setQuip] = useState('');
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchGenresAndQuip = async () => {
      try {
        const statsRes = await axios.get('/api/stats/me');
        const userStats = Array.isArray(statsRes.data)
          ? statsRes.data[0]
          : statsRes.data;
        setStats(userStats);

        const quipRes = await axios.post('/api/generate-quip', {
          statDescription: `Your top genres are ${userStats.topGenres.join(', ')}`,
        });
        setQuip(quipRes.data.quip);
      } catch (err) {
        setError('Failed to load top genres or quip.');
      }
    };
    fetchGenresAndQuip();
  }, []);

  const nextSlide = () => (window.location.href = '/slide5');

  if (error) {
    return (
      <PageTransition>
        <Text color="red.400">{error}</Text>
      </PageTransition>
    );
  }

  if (!stats) {
    return (
      <PageTransition>
        <Text>Loading genres...</Text>
      </PageTransition>
    );
  }

  const genreCount = stats.topGenres.length;
  const chartData = {
    labels: stats.topGenres,
    datasets: [
      {
        data: stats.topGenres.map(() => Math.floor(Math.random() * 100)),
        backgroundColor: Array(genreCount).fill('#1DB954'),
      },
    ],
  };

  return (
    <PageTransition>
      <Box
        minH="100vh"
        display="flex"
        flexDirection="column"
        alignItems="center"
        justifyContent="center"
        px={4}
      >
        <Text fontSize="3xl" fontWeight="bold" mb={6}>
          Your Top Genres
        </Text>
        <Box width={['90%', '70%', '40%']} mb={6}>
          <Pie data={chartData} />
        </Box>
        <Text color="spotify.green" fontStyle="italic" textAlign="center" mb={4}>
          {quip}
        </Text>
        <Button
          bg="spotify.green"
          color="spotify.black"
          _hover={{ bg: 'green.400', transform: 'scale(1.05)' }}
          onClick={nextSlide}
        >
          Next
        </Button>
      </Box>
    </PageTransition>
  );
}

export default Slide4_Genres;
