// src/pages/Slide9_HighestBpm.jsx
import React, { useEffect, useState } from 'react';
import { Box, Text, Button } from '@chakra-ui/react';
import axios from 'axios';
import PageTransition from '../components/PageTransition';

function Slide9_HighestBpm() {
  const [stats, setStats] = useState(null);
  const [quip, setQuip] = useState('');
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchAndQuip = async () => {
      try {
        const statsRes = await axios.get('/api/stats/me');
        const userStats = Array.isArray(statsRes.data) ? statsRes.data[0] : statsRes.data;
        setStats(userStats);

        // Generate quip about the highest BPM track
        const quipRes = await axios.post('/api/generate-quip', {
          statDescription: `Your highest BPM track is ${userStats.highestBpmTrack}. Get ready to move!`,
        });
        setQuip(quipRes.data.quip);
      } catch (err) {
        setError('Failed to load highest BPM track or quip.');
      }
    };
    fetchAndQuip();
  }, []);

  const nextSlide = () => (window.location.href = '/slide10');

  if (error) {
    return (
      <PageTransition>
        <Text color="red.300">{error}</Text>
      </PageTransition>
    );
  }

  if (!stats) {
    return (
      <PageTransition>
        <Text>Loading highest BPM track info...</Text>
      </PageTransition>
    );
  }

  return (
    <PageTransition>
      <Box
        minH="100vh"
        display="flex"
        flexDirection="column"
        alignItems="center"
        justifyContent="center"
        px={4}
        textAlign="center"
      >
        <Text fontSize="3xl" fontWeight="bold" mb={4}>
          Highest BPM Track
        </Text>
        <Text mb={6}>
          The track that really gets your heart pumping is <strong>{stats.highestBpmTrack}</strong>!
        </Text>
        <Text color="spotify.green" fontStyle="italic" mb={4}>
          {quip}
        </Text>
        <Button
          bg="spotify.green"
          color="spotify.black"
          _hover={{ bg: 'green.400', transform: 'scale(1.05)' }}
          onClick={nextSlide}
        >
          Next
        </Button>
      </Box>
    </PageTransition>
  );
}

export default Slide9_HighestBpm;
