import { createContext, useContext, useState, useEffect } from 'react';
import { fetchSpotifyDataWithQuip } from '../utils/api';

const SpotifyDataContext = createContext(null);

export const SpotifyDataProvider = ({ children }) => {
  const [spotifyData, setSpotifyData] = useState({
    topArtists: [], // Keep original format for Slide1
    topTracks: [],  // Keep original format for Slide2
    topAlbums: [],
    topGenres: [],
    listeningClock: {
      morning: [],
      afternoon: [],
      evening: [],
      night: []
    },
    monthlyTrends: Array(12).fill(0),
    longestStreak: 0,
    mostStreamedSong: '',
    highestBpmTrack: ''
  });
  const [quip, setQuip] = useState('');
  const [error, setError] = useState('');
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const data = await fetchSpotifyDataWithQuip();
        setSpotifyData(data.stats);
        setQuip(data.quip);
      } catch (err) {
        setError('Failed to load Spotify data');
      } finally {
        setIsLoading(false);
      }
    };
    fetchData();
  }, []);

  return (
    <SpotifyDataContext.Provider value={{ 
      spotifyData, 
      quip, 
      error, 
      isLoading,
      setQuip 
    }}>
      {children}
    </SpotifyDataContext.Provider>
  );
};

export const useSpotifyData = () => {
  const context = useContext(SpotifyDataContext);
  if (!context) {
    throw new Error('useSpotifyData must be used within a SpotifyDataProvider');
  }
  return context;
};
