import { createContext, useContext, useState, useEffect } from 'react';
import { fetchSpotifyDataWithQuip, fetchUserProfile } from '../utils/api';

const SpotifyDataContext = createContext(null);

export const SpotifyDataProvider = ({ children }) => {
  const [spotifyData, setSpotifyData] = useState(null);
  const [quip, setQuip] = useState('');
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const data = await fetchSpotifyDataWithQuip();
        setSpotifyData(data.stats);
        setQuip(data.quip); // Initial quip for Slide 1
      } catch (err) {
        setError(err);
      }
    };

    fetchData();
  }, []);

  return (
    <SpotifyDataContext.Provider value={{ spotifyData, quip, error }}>
      {children}
    </SpotifyDataContext.Provider>
  );
};

export const useSpotifyData = () => {
  const context = useContext(SpotifyDataContext);
  if (!context) {
    throw new Error('useSpotifyData must be used within a SpotifyDataProvider');
  }
  return context;
};
