// src/pages/Slide2_Tracks.jsx
import React, { useEffect, useState } from 'react';
import { Box, Text, Button } from '@chakra-ui/react';
import axios from 'axios';
import { Bar } from 'react-chartjs-2';
import PageTransition from '../components/PageTransition';

function Slide2_Tracks() {
  const [stats, setStats] = useState(null);
  const [quip, setQuip] = useState('');
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchTracksAndQuip = async () => {
      try {
        const statsRes = await axios.get('/api/stats/me');
        const userStats = Array.isArray(statsRes.data)
          ? statsRes.data[0]
          : statsRes.data;
        setStats(userStats);

        const quipRes = await axios.post('/api/generate-quip', {
          statDescription: `Your top tracks are ${userStats.topTracks.join(', ')}`,
        });
        setQuip(quipRes.data.quip);
      } catch (err) {
        setError('Failed to load top tracks or generate quip.');
      }
    };
    fetchTracksAndQuip();
  }, []);

  const nextSlide = () => (window.location.href = '/slide3');

  if (error) {
    return (
      <PageTransition>
        <Text color="red.400">{error}</Text>
      </PageTransition>
    );
  }

  if (!stats) {
    return (
      <PageTransition>
        <Text>Loading tracks...</Text>
      </PageTransition>
    );
  }

  // Example bar chart data
  const chartData = {
    labels: stats.topTracks,
    datasets: [
      {
        label: 'Top Tracks',
        data: stats.topTracks.map(() => Math.floor(Math.random() * 100)),
        backgroundColor: '#1DB954',
      },
    ],
  };

  return (
    <PageTransition>
      <Box
        minH="100vh"
        display="flex"
        flexDirection="column"
        alignItems="center"
        justifyContent="center"
        px={4}
      >
        <Text fontSize="3xl" fontWeight="bold" mb={6}>
          Your Top Tracks
        </Text>
        <Box width={['90%', '70%', '50%']} mb={6}>
          <Bar data={chartData} />
        </Box>
        <Text color="spotify.green" fontStyle="italic" textAlign="center" mb={4}>
          {quip}
        </Text>
        <Button
          bg="spotify.green"
          color="spotify.black"
          _hover={{ bg: 'green.400', transform: 'scale(1.05)' }}
          onClick={nextSlide}
        >
          Next
        </Button>
      </Box>
    </PageTransition>
  );
}

export default Slide2_Tracks;
