import React, { useEffect, useState } from 'react';
import { motion } from 'framer-motion';
import PageTransition from '../components/PageTransition';
import Navigation from '../components/Navigation';

function Slide8_MostStreamedSong() {
  const [stats, setStats] = useState(null);
  const [quip, setQuip] = useState('');
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchAndQuip = async () => {
      try {
        const statsRes = await fetch('/api/stats/me');
        const userStats = await statsRes.json();
        const stats = Array.isArray(userStats) ? userStats[0] : userStats;
        setStats(stats);

        const quipRes = await fetch('/api/generate-quip', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify({
            statDescription: `Your most streamed song is ${stats.mostStreamedSong}!`,
          }),
        });
        const quipData = await quipRes.json();
        setQuip(quipData.quip);
      } catch (err) {
        setError('Failed to load most streamed song or quip.');
      }
    };
    fetchAndQuip();
  }, []);

  if (error) {
    return (
      <PageTransition title="Error">
        <p className="text-red-400">{error}</p>
      </PageTransition>
    );
  }

  if (!stats) {
    return (
      <PageTransition title="Loading">
        <p>Loading most streamed song info...</p>
      </PageTransition>
    );
  }

  return (
    <PageTransition title="Most Streamed Song">
      <div className="flex flex-col items-center justify-center min-h-screen px-4 text-center">
        <motion.div
          initial={{ y: 20, opacity: 0 }}
          animate={{ y: 0, opacity: 1 }}
          transition={{ duration: 0.5 }}
        >
          <p className="mb-6">
            Your top track is <span className="font-bold">{stats.mostStreamedSong}</span>!
          </p>
          <p className="text-spotify-green italic">
            {quip}
          </p>
        </motion.div>
      </div>
      <Navigation currentSlide={8} />
    </PageTransition>
  );
}

export default Slide8_MostStreamedSong;
