// src/pages/Slide1_Artists.jsx
import React, { useEffect, useState } from 'react';
import { Box, Text, Button, VStack, List, ListItem } from '@chakra-ui/react';
import axios from 'axios';
import PageTransition from '../components/PageTransition';

function Slide1_Artists() {
  const [stats, setStats] = useState(null);
  const [quip, setQuip] = useState('');
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchDataAndQuip = async () => {
      try {
        // 1) Get stats
        const statsRes = await axios.get('/api/stats/me');
        const userStats = Array.isArray(statsRes.data)
          ? statsRes.data[0]
          : statsRes.data;
        setStats(userStats);

        // 2) Generate quip
        const quipRes = await axios.post('/api/generate-quip', {
          statDescription: `Your top artists are: ${userStats.topArtists.join(', ')}`,
        });
        setQuip(quipRes.data.quip);
      } catch (err) {
        setError('Could not fetch artists or generate quip.');
      }
    };
    fetchDataAndQuip();
  }, []);

  const nextSlide = () => (window.location.href = '/slide2');

  if (error) {
    return (
      <PageTransition>
        <Text color="red.300">{error}</Text>
      </PageTransition>
    );
  }

  if (!stats) {
    return (
      <PageTransition>
        <Text>Loading...</Text>
      </PageTransition>
    );
  }

  return (
    <PageTransition>
      <Box
        minH="100vh"
        display="flex"
        flexDirection="column"
        alignItems="center"
        justifyContent="center"
        px={4}
      >
        <VStack spacing={4}>
          <Text fontSize="3xl" fontWeight="bold">
            Your Top Artists
          </Text>
          <List spacing={2}>
            {stats.topArtists.map((artist) => (
              <ListItem key={artist}>{artist}</ListItem>
            ))}
          </List>
          <Text fontStyle="italic" color="spotify.green" textAlign="center">
            {quip}
          </Text>
          <Button
            onClick={nextSlide}
            bg="spotify.green"
            color="spotify.black"
            _hover={{ bg: 'green.400', transform: 'scale(1.05)' }}
          >
            Next
          </Button>
        </VStack>
      </Box>
    </PageTransition>
  );
}

export default Slide1_Artists;
