import React, { useState } from 'react';
import { motion } from 'framer-motion';
import { useNavigate } from 'react-router-dom';
import { useSpotifyData } from '../context/SpotifyDataContext';

function Dashboard() {
  const navigate = useNavigate();
  const { spotifyData, setSelectedYear, selectedYear } = useSpotifyData();
  const availableYears = ['2025', '2024', '2023', '2022'];

  const handleYearSelect = (year) => {
    setSelectedYear(year);
  };

  const startSlideshow = () => {
    navigate('/slide1');
  };

  return (
    <div className="min-h-screen flex flex-col items-center justify-center bg-gradient-to-b from-spotify-black to-black p-8">
      <motion.div
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        className="text-center space-y-8"
      >
        <h1 className="text-4xl font-bold text-spotify-white">
          Better Spotify Wrapped
        </h1>
        
        <div className="flex flex-col space-y-4">
          <h2 className="text-xl text-spotify-white">Select Your Year</h2>
          <div className="flex justify-center space-x-4">
            {availableYears.map((year) => (
              <motion.button
                key={year}
                whileHover={{ scale: 1.05 }}
                whileTap={{ scale: 0.95 }}
                onClick={() => handleYearSelect(year)}
                className={`px-6 py-3 rounded-full font-bold ${
                  selectedYear === year
                    ? 'bg-spotify-green text-spotify-black'
                    : 'bg-spotify-gray/30 text-spotify-white'
                }`}
              >
                {year}
              </motion.button>
            ))}
          </div>
        </div>

        <motion.button
          whileHover={{ scale: 1.05 }}
          whileTap={{ scale: 0.95 }}
          onClick={startSlideshow}
          disabled={!selectedYear}
          className="spotify-button disabled:opacity-50 disabled:cursor-not-allowed"
        >
          Start Your Wrapped
        </motion.button>
      </motion.div>
    </div>
  );
}

export default Dashboard;
