// src/pages/Slide3_Albums.jsx
import React, { useEffect, useState } from 'react';
import { Box, Text, Button, SimpleGrid } from '@chakra-ui/react';
import axios from 'axios';
import PageTransition from '../components/PageTransition';

function Slide3_Albums() {
  const [stats, setStats] = useState(null);
  const [quip, setQuip] = useState('');
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchAlbumsAndQuip = async () => {
      try {
        const statsRes = await axios.get('/api/stats/me');
        const userStats = Array.isArray(statsRes.data)
          ? statsRes.data[0]
          : statsRes.data;
        setStats(userStats);

        const quipRes = await axios.post('/api/generate-quip', {
          statDescription: `Your top albums are ${userStats.topAlbums.join(', ')}`,
        });
        setQuip(quipRes.data.quip);
      } catch (err) {
        setError('Failed to load top albums or generate quip.');
      }
    };
    fetchAlbumsAndQuip();
  }, []);

  const nextSlide = () => (window.location.href = '/slide4');

  if (error) {
    return (
      <PageTransition>
        <Text color="red.400">{error}</Text>
      </PageTransition>
    );
  }

  if (!stats) {
    return (
      <PageTransition>
        <Text>Loading albums...</Text>
      </PageTransition>
    );
  }

  return (
    <PageTransition>
      <Box
        minH="100vh"
        display="flex"
        flexDirection="column"
        alignItems="center"
        justifyContent="center"
        px={4}
      >
        <Text fontSize="3xl" fontWeight="bold" mb={4}>
          Your Top Albums
        </Text>
        <SimpleGrid columns={[1, 2, 3]} spacing={4} mb={6}>
          {stats.topAlbums.map((album) => (
            <Box
              key={album}
              borderWidth="1px"
              borderColor="spotify.gray"
              p={4}
              rounded="md"
              textAlign="center"
            >
              {album}
            </Box>
          ))}
        </SimpleGrid>
        <Text color="spotify.green" fontStyle="italic" textAlign="center" mb={4}>
          {quip}
        </Text>
        <Button
          bg="spotify.green"
          color="spotify.black"
          _hover={{ bg: 'green.400', transform: 'scale(1.05)' }}
          onClick={nextSlide}
        >
          Next
        </Button>
      </Box>
    </PageTransition>
  );
}

export default Slide3_Albums;
