import React, { useState } from 'react';
import { motion } from 'framer-motion';
import { useSpotifyData } from '../context/SpotifyDataContext';

function Dashboard() {
  const [selectedYear, setSelectedYear] = useState('2024');
  const { updateYear } = useSpotifyData();

  const years = ['2024', '2023', '2022'];

  const handleYearSelect = async (year) => {
    setSelectedYear(year);
    await updateYear(year);
  };

  return (
    <div className="min-h-screen flex flex-col items-center justify-center">
      <h1 className="text-4xl font-bold mb-8">Better Spotify Wrapped</h1>
      
      <div className="flex gap-4 mb-8">
        {years.map((year) => (
          <motion.button
            key={year}
            whileHover={{ scale: 1.05 }}
            whileTap={{ scale: 0.95 }}
            className={`px-6 py-3 rounded-full font-bold ${
              selectedYear === year 
                ? 'bg-spotify-green text-black' 
                : 'bg-spotify-gray/30 text-white'
            }`}
            onClick={() => handleYearSelect(year)}
          >
            {year}
          </motion.button>
        ))}
      </div>

      <motion.button
        whileHover={{ scale: 1.05 }}
        whileTap={{ scale: 0.95 }}
        className="spotify-button"
        onClick={() => navigate('/slide1')}
      >
        Start Your Journey
      </motion.button>
    </div>
  );
}
