import React, { useEffect, useState } from 'react';
import { motion } from 'framer-motion';
import PageTransition from '../components/PageTransition';
import Navigation from '../components/Navigation';

function Slide1_Artists() {
  const [stats, setStats] = useState(null);
  const [quip, setQuip] = useState('');
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchDataAndQuip = async () => {
      try {
        const statsRes = await fetch('/api/stats/me');
        const userStats = await statsRes.json();
        const stats = Array.isArray(userStats) ? userStats[0] : userStats;
        setStats(stats);

        const quipRes = await fetch('/api/generate-quip', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify({
            statDescription: `Your top artists are: ${stats.topArtists.join(', ')}`,
          }),
        });
        const quipData = await quipRes.json();
        setQuip(quipData.quip);
      } catch (err) {
        setError('Could not fetch artists or generate quip.');
      }
    };
    fetchDataAndQuip();
  }, []);

  if (error) {
    return (
      <PageTransition title="Error">
        <p className="text-red-400">{error}</p>
      </PageTransition>
    );
  }

  if (!stats) {
    return (
      <PageTransition title="Loading">
        <p>Loading...</p>
      </PageTransition>
    );
  }

  return (
    <PageTransition title="Your Top Artists">
      <div className="flex flex-col items-center justify-center min-h-screen px-4">
        <div className="space-y-4">
          <ul className="space-y-2">
            {stats.topArtists.map((artist) => (
              <motion.li
                key={artist}
                initial={{ opacity: 0, y: 20 }}
                animate={{ opacity: 1, y: 0 }}
                className="text-center"
              >
                {artist}
              </motion.li>
            ))}
          </ul>
          <p className="italic text-spotify-green text-center">
            {quip}
          </p>
        </div>
      </div>
      <Navigation currentSlide={1} />
    </PageTransition>
  );
}

export default Slide1_Artists;
