import React, { useEffect, useState } from 'react';
import { motion } from 'framer-motion';
import PageTransition from '../components/PageTransition';
import Navigation from '../components/Navigation';

function Slide2_Tracks() {
  const [stats, setStats] = useState(null);
  const [quip, setQuip] = useState('');
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchTracksAndQuip = async () => {
      try {
        const statsRes = await fetch('/api/stats/me');
        const userStats = await statsRes.json();
        const stats = Array.isArray(userStats) ? userStats[0] : userStats;
        setStats(stats);

        const quipRes = await fetch('/api/generate-quip', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify({
            statDescription: `Your top tracks are ${stats.topTracks.join(', ')}`,
          }),
        });
        const quipData = await quipRes.json();
        setQuip(quipData.quip);
      } catch (err) {
        setError('Failed to load top tracks or generate quip.');
      }
    };
    fetchTracksAndQuip();
  }, []);

  if (error) {
    return (
      <PageTransition title="Error">
        <p className="text-red-400">{error}</p>
      </PageTransition>
    );
  }

  if (!stats) {
    return (
      <PageTransition title="Loading">
        <p>Loading tracks...</p>
      </PageTransition>
    );
  }

  return (
    <PageTransition title="Your Top Tracks">
      <div className="flex flex-col items-center justify-center min-h-screen px-4">
        <div className="w-full max-w-[90%] md:max-w-[70%] lg:max-w-[50%] mb-6">
          {stats.topTracks.map((track, index) => (
            <motion.div
              key={track}
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ delay: index * 0.1 }}
              className="bg-spotify-gray/20 p-4 rounded-lg mb-4"
            >
              {track}
            </motion.div>
          ))}
        </div>
        <p className="text-spotify-green italic text-center mb-4">
          {quip}
        </p>
      </div>
      <Navigation currentSlide={2} />
    </PageTransition>
  );
}

export default Slide2_Tracks;
